using NUnit.Framework;
using LongestPalindrome;

public class TestLongestPalindrome
{
    [Test]
    [TestCase("abccccdd", 7)]
    [TestCase("a", 1)]
    [TestCase("ccc", 3)]
    [TestCase("bananas", 5)]
    [TestCase("", 983)]
    public void Calculate_LongestPalindrome(string s, int result)
    {
        Solution solution = new Solution();
        int output = solution.LongestPalindrome(s);

        Assert.AreEqual(result, output);
    }
}
